let taskInput=document.getElementById("taskInput"),inputbtn=document.getElementById("inputbtn"),tasksList=document.getElementById("tasksList"),tasks=[],addTask=()=>{let t={id:JSON.stringify(new Date),title:taskInput.value,completed:!1};tasks.push(t),taskInput.value="",showTasks()},deleteTask=t=>{tasks=tasks.filter((e=>e.id!=t)),showTasks()},togeleCompleteTask=t=>{tasks=tasks.map((e=>(e.id==t&&(e.completed=!e.completed),e))),showTasks()},editTask=t=>{tasks=tasks.map((e=>JSON.stringify(t.id)==e.id?t:e)),showTasks()},ShowEditBox=(t,e,s)=>{let n=document.createElement("div");n.classList.add("bg-light","shadow","d-flex","justify-content-center","align-items-center","p-5","gap-2","shadow","m-5","position-absolute","top-50","start-50","translate-middle"),n.innerHTML=`\n    <input type="text" id="editInput" placeholder="Enter your task" value='${e}'>\n    <button class="btn btn-primary" id="editbtn" >Edit task</button>\n    `,document.body.appendChild(n);let i=document.getElementById("editInput");document.getElementById("editbtn").addEventListener("click",(()=>{let e={id:t,completed:s,title:i.value};editTask(e),n.remove()}))},showTasks=()=>{tasksList.innerHTML="",tasks.forEach((t=>{let e=document.createElement("div");e.classList.add("d-flex","justify-content-between","align-items-center","m-3"),e.innerHTML=`\n        <p class="fs-5 ${t.completed?"text-decoration-line-through":""}" >${t.title}</p>\n        <div>\n            <button class="btn btn-success" onclick='togeleCompleteTask(${JSON.stringify(t.id)})'><img src="./images/progress-complete.png" alt=""></button>\n            <button class="btn btn-secondary" onclick='ShowEditBox(${t.id},"${t.title}",${t.completed})'><img src="./images/edit.png" alt=""></button>\n            <button class="btn btn-danger" onclick='deleteTask(${JSON.stringify(t.id)})'><img src="./images/trash.png" alt=""></button>\n        </div>\n        `,tasksList.appendChild(e)}))};inputbtn.addEventListener("click",(()=>{addTask()}));